package java_prgm;

public class Increment_Decrement_Operator {

	public static void main(String[] args) {
		int a=5;
		a++;
		a++;		
		System.out.println(a);
		System.out.println(a++);
		System.out.println(--a);
		System.out.println(a--);
		System.out.println(a++);
		System.out.println(++a);

		System.out.println(a++);

		System.out.println(a);
		System.out.println(a++);
		System.out.println(a--);
		System.out.println(a--);
	


	
		// TODO Auto-generated method stub

	}

}
