package java_prgm;

public class Reverse_prgn_Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{ if(i==3||i==6)
			
				for(int j=1;j<=i;j++)
				{
					System.out.println(i);
		
		}
		else
		{
			System.out.println(i);
		}
}
	}
}
